import React from 'react';


class Question extends React.Component {

  constructor(){
    super()
    this.state = {
      result: null
    }

  }

  componentDidMount(){
    this.setState({
      result: this.props.answer
    })
  }




 validateAnswer = event => (console.log(this.state.result));

  render() {
    color = event =>
      {event.target.value === this.state.result? {background: '#FF5733'} : {background: '#DAF7A6'}}


    return(
      <div className= 'question-frame'>
        <h2>{this.props.text}</h2>
        <button style={color()} type="button"  value='true' onClick={this.validateAnswer}>True</button><span/>
        <button style={color()} type="button"  value='false' onClick={this.validateAnswer}>False</button>
      </div>
    )
  }
}

export default Question;
