import React from 'react';


class Question extends React.Component {






 validateAnswer = event => ((event.target.value === this.state.result)? (console.log('yes')) : (console.log('no')));
//  btnColor = event => (event.target.value === this.state.result? ({background: '#FF5733'}) : ({background: '#DAF7A6'}))


 render() {

   return(
      <div className= 'question-frame'>
        <h2>{this.props.text}</h2>
        <button  type="button"  value='true' onClick={this.validateAnswer}>True</button><span/>
        <button  type="button"  value='false' onClick={this.validateAnswer}>False</button>
      </div>
    )
  }
}

export default Question;
