import React from 'react';


class Question extends React.Component {
constructor(){
  super()
  this.state = {
    trueBtn: null,
    falseBtn: null
  }
}



  btnColor = event => (
    this.props.answer+"" === event.target.value? {background: '#FF5733'} : {background: '#DAF7A6'}
  )

  handleClick = event => {
    this.setState({
      trueBtn: this.btnColor,
      falseBtn: this.btnColor
    })
    console.log(this.btnColor)
  }



 render() {

   return(
      <div className= 'question-frame'>
        <h2>{this.props.text}</h2>
        <button  style={this.state.trueBtn} type="button"  value='true' onClick={this.handleClick}>True</button><span/>
        <button  style={this.state.falseBtn} type="button"  value='false' onClick={this.handleClick}>False</button>
      </div>
    )
  }
}

export default Question;
