import React from 'react';


class Question extends React.Component {
constructor(){
  super()
  this.state = {
    answeredCorrectly: null
  }
}

  // btnColor = (event) => this.props.answer+"" === event.target.value? {background: '#FF5733'} : {background: '#DAF7A6'}

  handleClick = event => {
    event.persist();
    if (this.state.answeredCorrectly !== null) {}
    this.setState({
      answeredCorrectly: this.props.answer+"" === event.target.value ? true : false,
    }, () => {
      return this.state.answeredCorrectly ? event.target.style.background = "green" : event.target.style.background = "red"
    });
  }

 render() {
   return(
      <div className= 'question-frame'>
        <h2>{this.props.text}</h2>
        <button type="button"  value='true' onClick={this.handleClick}>True</button><span/>
        <button type="button"  value='false' onClick={this.handleClick}>False</button>
      </div>
    )
  }
}

export default Question;
