import React from 'react';


class Question extends React.Component {




  btnColor = event => {this.props.answer+"" === event.target.value? {background: '#FF5733'} : {background: '#DAF7A6'}}
  validateAnswer = event => (btnColor())


 render() {

   return(
      <div className= 'question-frame'>
        <h2>{this.props.text}</h2>
        <button  style={this.btnColor} type="button"  value='true' onClick={this.validateAnswer}>True</button><span/>
        <button  style={this.btnColor} type="button"  value='false' onClick={this.validateAnswer}>False</button>
      </div>
    )
  }
}

export default Question;
